        // 初始化
        function init() {
            renderItemList();
            renderQuestion();
            startTimer();
            setupEventListeners();
        }

        // 渲染答题卡
        function renderItemList() {
            itemList.innerHTML = '';
            Twf5GT75voBdnQ.forEach((q, idx) => {
                const li = document.createElement('li');
                li.textContent = q.id;
                li.className = q.answered ? '' : 'unanswered';
                if (q.marked) li.classList.add('marked');
                if (q.notes) li.classList.add('has-notes'); // 有笔记的题目显示✏️图标
                li.onclick = () => switchQuestion(idx);
                itemList.appendChild(li);
            });
        }

        // 渲染当前问题
        function renderQuestion() {
            const q = Twf5GT75voBdnQ[currentQ];
            currentItemEl.textContent = q.uworldid; //q.id;
            currentNumEl.textContent = q.id;
            
            // 渲染题目文本，处理多个高亮
            let html = q.stem;
            // 对每个高亮文本，找出所有出现的位置
            const replacements = [];
            q.highlightedTexts.forEach(text => {
                let index = 0;
                while (index < html.length) {
                    const pos = html.indexOf(text, index);
                    if (pos === -1) break;
                    replacements.push({
                        text,
                        start: pos,
                        end: pos + text.length
                    });
                    index = pos + text.length;
                }
            });

            // 按起始位置从大到小排序（这样替换时不会影响前面的索引）
            replacements.sort((a, b) => b.start - a.start);

            // 执行替换
            replacements.forEach(r => {
                html = html.substring(0, r.start) + 
                       '<mark class="highlight" data-text="' + r.text + '">' + 
                       r.text + 
                       '</mark>' + 
                       html.substring(r.end);
            });
            
            questionStem.innerHTML = html;
            
            // 为高亮文本添加点击事件
            document.querySelectorAll('.highlight').forEach(highlight => {
                highlight.onclick = (e) => {
                    e.stopPropagation();
                    const text = highlight.dataset.text;
                    removeHighlight(text);
                };
            });
            
            // 渲染选项
            optionsContainer.innerHTML = '';
			optionsContainer.innerHTML = q.header + optionsContainer.innerHTML;
            q.options.forEach((option, idx) => {
                const div = document.createElement('div');
                div.className = 'option';
                if (q.strikethroughOptions && q.strikethroughOptions.includes(option[0])) {
                    div.classList.add('strikethrough');
                }
                
                const input = document.createElement('input');
                input.type = 'radio';
                input.name = `q${q.id}`;
                input.value = option[0];
                input.checked = q.selected === option[0]; // 恢复已选答案
                input.onchange = (e) => {
                    q.selected = e.target.value;
                    q.answered = true;
                    renderItemList();
                };
                
                const label = document.createElement('label');
                label.textContent = option;
                
                // 添加双击事件处理删除线
                label.ondblclick = () => {
                    toggleStrikethrough(option[0]);
                };
                
                div.appendChild(input);
                div.appendChild(label);
                optionsContainer.appendChild(div);
            });
            
            // 重置答案解析显示状态
            answerExplanation.style.display = 'none';
            
            // 更新笔记内容
            notesText.value = q.notes;
        }

        // 切换问题
        function switchQuestion(idx) {
            // 关闭所有悬浮窗
            closeAllFloatingWindows();
            
            currentQ = idx;
            renderQuestion();
        }

        // 启动计时器
        function startTimer() {
            timerInterval = setInterval(() => {
                if (timer <= 0) {
                    clearInterval(timerInterval);
                    showResults();
                    return;
                }
                
                timer--;
                const minutes = Math.floor(timer / 60);
                const seconds = timer % 60;
                const timeStr = `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
                
                blockTimeEl.textContent = `Block Time Remaining: ${timeStr}`;
                
                // 5分钟警告
                if (timer === 300 && !warned5min) {
                    blockTimeEl.classList.add('blink');
                    warned5min = true;
                }
                
                // 1分钟警告
                if (timer === 60 && !warned1min) {
                    blockTimeEl.classList.add('blink');
                    warned1min = true;
                }
            }, 1000);
        }

