        // 处理计算器输入
        function handleCalculatorInput(val) {
            let display = calcDisplay.textContent;
            
            switch (val) {
                case '=':
                    try {
                        calcDisplay.textContent = eval(display);
                    } catch (e) {
                        calcDisplay.textContent = 'Error';
                    }
                    break;
                case 'C':
                    calcDisplay.textContent = '';
                    break;
                case '+/-':
                    calcDisplay.textContent = (-parseFloat(display)).toString();
                    break;
                case 'sqrt':
                    calcDisplay.textContent = Math.sqrt(parseFloat(display)).toString();
                    break;
                case '1/x':
                    calcDisplay.textContent = (1 / parseFloat(display)).toString();
                    break;
                default:
                    calcDisplay.textContent += val;
                    break;
            }
        }

        // 选项删除线功能
        function toggleStrikethrough(optionValue) {
            const q = Twf5GT75voBdnQ[currentQ];
            if (!q.strikethroughOptions) {
                q.strikethroughOptions = [];
            }
            
            const index = q.strikethroughOptions.indexOf(optionValue);
            if (index === -1) {
                q.strikethroughOptions.push(optionValue);
            } else {
                q.strikethroughOptions.splice(index, 1);
            }
            
            renderQuestion();
        }

        // 题目文本高亮功能
        function setupHighlightFeature() {
            questionStem.addEventListener('mouseup', () => {
                const selection = window.getSelection();
                const selectedText = selection.toString().trim();
                if (selectedText) {
                    const q = Twf5GT75voBdnQ[currentQ];
                    // 检查是否已经高亮
                    if (!q.highlightedTexts.includes(selectedText)) {
                        q.highlightedTexts.push(selectedText);
                        renderQuestion();
                    }
                }
            });
        }
        
        // 移除高亮
        function removeHighlight(text) {
            const q = Twf5GT75voBdnQ[currentQ];
            const index = q.highlightedTexts.indexOf(text);
            if (index !== -1) {
                q.highlightedTexts.splice(index, 1);
                renderQuestion();
            }
        }

